import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { Search, Book } from 'lucide-react'
import { useStories } from '../context/StoryContext'

const StoryList: React.FC = () => {
  const { stories } = useStories()
  const [searchTerm, setSearchTerm] = useState('')

  const filteredStories = stories.filter(
    (story) =>
      story.title.toLowerCase().includes(searchTerm.toLowerCase())
  )

  return (
    <div>
      <h1 className="text-4xl font-bold mb-6 text-center text-primary">Explore Exciting Stories!</h1>
      <div className="mb-8 relative">
        <input
          type="text"
          placeholder="Search stories by title..."
          className="input pl-12 text-lg"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Search className="absolute left-4 top-4 text-primary" size={24} />
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredStories.map((story) => (
          <Link
            key={story.id}
            to={`/story/${story.id}`}
            className="card transform hover:scale-105 transition duration-300"
          >
            <div className="p-6">
              <h2 className="text-2xl font-semibold mb-2 text-primary">{story.title}</h2>
              <p className="text-text-light mb-4 flex items-center">
                <Book className="mr-2" size={20} />
                Read Story
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  )
}

export default StoryList
